// Emoji mappings for visual feedback
const sentimentEmojis = {
  positive: ['', '', '', '', '', '', '', ''],
  negative: ['', '', '', '', '', '', '', ''],
  neutral: ['', '', '', '', '', '', '', '']
};

const sentimentMessages = {
  positive: [
    "Wow! These words make me feel happy! ",
    "This sounds super positive! Great job! ",
    "I can feel the good vibes from these words! ",
    "This makes me want to smile! These are happy words! "
  ],
  negative: [
    "Oh no! These words sound sad or angry. ",
    "I can sense some not-so-happy feelings here. ",
    "These words might make someone feel upset. ",
    "Hmm, these words have some tough feelings in them. "
  ],
  neutral: [
    "These words are pretty neutral - not too happy or sad! ",
    "I feel calm and balanced with these words. ",
    "These words are right in the middle - not strongly emotional. ",
    "Pretty chill words! Not too exciting, not too sad. "
  ]
};

export function analyzeSentiment(text: string): SentimentResult {
  if (!text.trim()) {
    return {
      sentiment: 'neutral',
      score: 0,
      confidence: 0,
      matchedWords: [],
      emoji: '',
      message: "Try typing some words and I'll tell you how they feel!"
    };
  }

  // Convert to lowercase and split into words
  const words = text.toLowerCase()
    .replace(/[^\w\s]/g, ' ')
    .split(/\s+/)
    .filter(word => word.length > 0);

  // Count matches in each category
  const positiveMatches = words.filter(word => positiveWords.includes(word));
  const negativeMatches = words.filter(word => negativeWords.includes(word));
  const neutralMatches = words.filter(word => neutralWords.includes(word));

  // Calculate scores
  const positiveScore = positiveMatches.length;
  const negativeScore = negativeMatches.length;
  const neutralScore = neutralMatches.length;
  const totalMatches = positiveScore + negativeScore + neutralScore;

  // Determine sentiment - prioritize emotional words over neutral
  let sentiment: 'positive' | 'negative' | 'neutral';
  let matchedWords: string[];
  let score: number;

  // If we have any positive or negative matches, prioritize them over neutral
  if (positiveScore > 0 || negativeScore > 0) {
    if (positiveScore > negativeScore) {
      sentiment = 'positive';
      matchedWords = positiveMatches;
      score = positiveScore;
    } else if (negativeScore > positiveScore) {
      sentiment = 'negative';
      matchedWords = negativeMatches;
      score = negativeScore;
    } else {
      // Tie between positive and negative - default to positive for kids' app
      sentiment = 'positive';
      matchedWords = positiveMatches;
      score = positiveScore;
    }
  } else if (neutralScore > 0) {
    sentiment = 'neutral';
    matchedWords = neutralMatches;
    score = neutralScore;
  } else {
    // No matches found - analyze general tone
    sentiment = 'neutral';
    matchedWords = [];
    score = 0;
  }

  // Calculate confidence based on ratio of matches to total words
  const confidence = totalMatches > 0 ? Math.min((totalMatches / words.length) * 100, 100) : 50;

  // Get random emoji and message
  const emoji = sentimentEmojis[sentiment][Math.floor(Math.random() * sentimentEmojis[sentiment].length)];
  const message = sentimentMessages[sentiment][Math.floor(Math.random() * sentimentMessages[sentiment].length)];

  return {
    sentiment,
    score,
    confidence: Math.round(confidence),
    matchedWords,
    emoji,
    message
  };
}

// Fun fact generator for kids
export function getFunFact(sentiment: 'positive' | 'negative' | 'neutral'): string {
  const facts = {
    positive: [
      "Did you know that smiling can actually make you feel happier? Even fake smiles work! ",
      "The word 'happiness' comes from the Old Norse word 'happ' which means luck or chance! ",
      "Scientists say that positive words can actually change your brain! ",
      "When you say nice words, your brain releases happy chemicals called endorphins! "
    ],
    negative: [
      "It's totally normal to feel sad sometimes - even robots like me have down days! ",
      "Did you know that crying can actually help you feel better? Tears release stress! ",
      "Even negative words are important - they help us understand and talk about difficult feelings! ",
      "Remember: feeling upset is temporary, but learning about emotions lasts forever! "
    ],
    neutral: [
      "Neutral words are like the calm in between storms - they give our emotions a rest! ",
      "Did you know that 'okay' is one of the most recognized words in the world? ",
      "Neutral feelings are important too - they help us think clearly and make good decisions! ",
      "Sometimes being calm and neutral is exactly what we need! It's like a mental break! "
    ]
  };
  
  return facts[sentiment][Math.floor(Math.random() * facts[sentiment].length)];
}

// Generate practice words for exercises
export function getRandomWords(sentiment: 'positive' | 'negative' | 'neutral', count: number = 5): string[] {
  const wordLists = {
    positive: positiveWords,
    negative: negativeWords,
    neutral: neutralWords
  };
  
  const words = [...wordLists[sentiment]];
  const result: string[] = [];
  
  for (let i = 0; i < count && words.length > 0; i++) {
    const randomIndex = Math.floor(Math.random() * words.length);
    result.push(words.splice(randomIndex, 1)[0]);
  }
  
  return result;
}